function Player(id, socket) {
	this.id = id;
	this.room = null;

	this.x = null;
	this.y = null;

	this.getSocket = function () {
		return socket;
	};
}

Object.defineProperty(Player.prototype, 'socket', {
	get: function () {
		return this.getSocket();
	}
});

Player.prototype.send = function (packet) {
	console.log("Sending data to " + this.id + ": " + JSON.stringify(packet));
	this.socket.write(packet);
};

Player.prototype.destroy = function () {
	if (this.room !== null) {
		this.room.removePlayer(this);
	}
};

Player.prototype.updatePosition = function (x, y) {
	this.x = x;
	this.y = y;
};

Player.prototype.resetPosition = function () {
	this.x = null;
	this.y = null;
};

module.exports = Player;