var util = require('util');
var AbstractRouter = require('router/AbstractRouter');
var GameHandler = require('handlers/GameHandler');

function MilkRouter() {
	AbstractRouter.call(this);

	this.addHandler('joinRoom', GameHandler.joinRoomHandler);
	this.addHandler('leaveRoom', GameHandler.leaveRoomHandler);
	this.addHandler('move', GameHandler.moveHandler);
}

util.inherits(MilkRouter, AbstractRouter);
module.exports = MilkRouter;